void keyboard_post_init_user(void) {
    is_caps_lock_on = false;
}

bool process_record_user(uint16_t keycode, keyrecord_t *record) {
    switch (keycode) {
        case KC_CAPSLOCK:
            if (record->event.pressed) {
                if (is_caps_lock_on) {
                    is_caps_lock_on = false;
                } else {
                    is_caps_lock_on = true;
                }
            }
            break;
        case LOWER:
            if (record->event.pressed) {
                layer_on(_LOWER);
                update_tri_layer(_LOWER, _RAISE, _ADJUST);
            } else {
                layer_off(_LOWER);
                update_tri_layer(_LOWER, _RAISE, _ADJUST);
            }
            return false;
            break;
        case RAISE:
            if (record->event.pressed) {
                layer_on(_RAISE);
                update_tri_layer(_LOWER, _RAISE, _ADJUST);
            } else {
                layer_off(_RAISE);
                update_tri_layer(_LOWER, _RAISE, _ADJUST);
            }
            return false;
            break;
        case ADJUST:
            if (record->event.pressed) {
                layer_on(_ADJUST);
            } else {
                layer_off(_ADJUST);
            }
            return false;
            break;
        case KC_IDLE:
            if (record->event.pressed) {
                if (idle_enabled == true) {
                    idle_enabled = false;
                    //idle_timer   = timer_max;
                    oled_clear();
                } else {
                    idle_enabled = true;
                    //idle_timer   = timer_max;
                };
            };
            break;
        default:
            break;
    }

    return true;
}

#ifdef ENCODER_ENABLE
bool encoder_update_user(uint8_t index, bool clockwise) {
    switch (index)
    {
    case 0:
        if (clockwise) {
            tap_code_delay(KC_VOLD, 10);
        } else {
            tap_code_delay(KC_VOLU, 10);
        }
        break;
    case 1:
        if (clockwise) {
            tap_code_delay(KC_LEFT, 10);
        } else {
            tap_code_delay(KC_RIGHT, 10);
        }
        break;
    default:
        break;
    }
    return false;
}
#endif

