void render_cat_dance(void) {
    if (idle_enabled) {
        oled_set_cursor(0,0);
        if (timer_elapsed32(animation_timer_current_time) > ANIM_FRAME_DURATION) {
            tap_code(KC_F15);
            animation_timer_current_time = timer_read32();
            curr_anim_frame = (curr_anim_frame + 1) % ANIM_FRAMES;
            oled_write_raw_P(DanceAnimation[abs((ANIM_FRAMES - 1) - curr_anim_frame)], ANIM_SIZE);
        }
    }
}

void render_animation_frame(oled_animation_t animation, uint8_t frame) {
    oled_set_cursor(animation.col, animation.line);
    oled_write_raw_P(animation.frames[frame], animation.size);
}

